mapper.select:
select count(*)

from tbl_calendar



        name like '%' #{name} '%'


        and description like '%' #{description} '%'


        and startDate > #{startDate}


        and endDate < #{endDate}


        and loginAct = #{loginAct}



order by createTime desc

select id, name, startDate, endDate, description, url
from tbl_calendar
loginAct = #{loginAct}
and name like '%' #{name} '%'
and description like '%' #{description} '%'
and startDate > #{startDate}
and endDate < #{endDate}
order by createTime desc limit #{skipCount} , #{pageSize}

mapper.delete:
delete from tbl_calendar
where id in

    #{ids}
