mapper.select:
select id, loginAct, `name`, `group`, startDate, endDate, description
from tbl_plan
`name` like '%' #{name} '%'
loginAct like '%' #{loginAct} '%'
`group` like '%' #{group} '%'
startDate > #{startDate}
endDate < #{endDate}
description like '%' #{description} '%'
order by createTime desc limit #{skipCount} , #{pageSize}
